CREATE TABLE CUSTOMER (PERSONALID VARCHAR(255) NOT NULL, NAME VARCHAR(255), SURNAME VARCHAR(255), PRIMARY KEY (PERSONALID))
CREATE TABLE DRUG (ID BIGINT NOT NULL, DESCRIPTION VARCHAR(255), NAME VARCHAR(255), SOLDQUANTITY BIGINT, PRIMARY KEY (ID))
CREATE TABLE EMPLOYEE (ID BIGINT NOT NULL, BORN DATE, CITY VARCHAR(255), EMAIL VARCHAR(255), NAME VARCHAR(255), PHONE VARCHAR(255), STREET VARCHAR(255), SURNAME VARCHAR(255), BRANCH_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE INSURANCE (ID BIGINT NOT NULL, CITY VARCHAR(255), EMAIL VARCHAR(255), NAME VARCHAR(255), PHONE VARCHAR(255), STREET VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE RECIPE (ID BIGINT NOT NULL, CHARGE BIGINT, LIMIT BIGINT, PRESCRIPTIONNUMBER VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE SUPPLIER (ID BIGINT NOT NULL, CITY VARCHAR(255), EMAIL VARCHAR(255), NAME VARCHAR(255), PHONE VARCHAR(255), STREET VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE SUPPLIES (ID BIGINT NOT NULL, COST BIGINT, DRUG_ID BIGINT, SUPPLIER_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE WAREHOUSE (ID BIGINT NOT NULL, CITY VARCHAR(255), EMAIL VARCHAR(255), NAME VARCHAR(255), PHONE VARCHAR(255), STREET VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE WORKPLACE (ID BIGINT NOT NULL, CITY VARCHAR(255), EMAIL VARCHAR(255), NAME VARCHAR(255), PHONE VARCHAR(255), STREET VARCHAR(255), PRIMARY KEY (ID))
ALTER TABLE EMPLOYEE ADD CONSTRAINT FK_EMPLOYEE_BRANCH_ID FOREIGN KEY (BRANCH_ID) REFERENCES WORKPLACE (ID)
ALTER TABLE SUPPLIES ADD CONSTRAINT FK_SUPPLIES_DRUG_ID FOREIGN KEY (DRUG_ID) REFERENCES DRUG (ID)
